mapper.insert:
insert into message (id, name, content, 
  status, create_at, update_at, 
  update_by, create_by, title
  )
values (#{id,jdbcType=BIGINT}, #{name,jdbcType=VARCHAR}, #{content,jdbcType=VARCHAR}, 
  #{status,jdbcType=INTEGER}, #{createAt,jdbcType=BIGINT}, #{updateAt,jdbcType=BIGINT},
  #{updateBy,jdbcType=VARCHAR}, #{createBy,jdbcType=VARCHAR}, #{title,jdbcType=VARCHAR}
  )

mapper.select:
select * from message


    name like CONCAT('%','${name}','%')


  and status = #{status}
